// Hamburger menu
$(document).ready(function() {
    var navigatioMenu = $(".navigationMenu");
    var trigger = $(".trigger");
    var tlIn = new TimelineMax({ paused: true, onComplete: onComplete });
    var tlOut = new TimelineMax({ paused: true, onComplete: onComplete });

    var li = $(navigatioMenu).find(".menu li");
    var revealer = $(navigatioMenu).find(".revealer");
    var menu = $(navigatioMenu).find(".menu");
    var menuWrapper = $(navigatioMenu).find(".menu-wrapper");

    var easeInOut = Circ.easeInOut;
    var easeIn = Circ.easeIn;
    var easeOut = Circ.easeOut;
    var speed = 0.5;

    var status = "close";
    var canAnimate = true;

    /** Set Menu Height */
    function setMenuHeight() {
        $(menuWrapper).slimscroll({
            height: document.documentElement.clientHeight - 1,
            color: "transparent"
        });
    }
    setMenuHeight();
    $(window).resize(function() {
        setMenuHeight();
    });

    function onComplete() {
        $(trigger).toggleClass("active");
        canAnimate = true;
        $(navigatioMenu).toggleClass("active");
    }

    /* Timeline In Play */

    tlIn
        .fromTo(
            revealer,
            speed, { x: "0%", width: "0%", ease: easeIn }, { width: "100%", ease: easeInOut }
        )
        .to(revealer, speed, { x: "100%", ease: easeOut })
        .set(revealer, { x: "0%", width: "0%" })
        .fromTo(
            menuWrapper,
            speed, { x: "-100%", ease: easeIn }, { x: "0%", ease: easeOut },
            "-=0.5"
        ).staggerFromTo(li, speed, { x: "-120%" }, { x: "0%" }, 0.05, "-=0.5");

    tlOut
        .fromTo(
            revealer,
            speed, { x: "0%", width: "0%", ease: easeIn }, { width: "100%", ease: easeInOut }
        )
        .to(revealer, speed, { x: "100%", ease: easeOut })
        .set(revealer, { x: "0%", width: "0%" })
        .to(menuWrapper, speed, { x: "100%", ease: easeOut }, "-=0.5")
        .set(menuWrapper, { x: "-100%" });

    trigger.on("click", toggleMenu);

    function toggleMenu() {
        if (canAnimate === true) {
            canAnimate = false;
            if (status == "close") {
                console.log(status);
                tlIn.play(0);
                status = "open";
            } else {
                console.log(status);
                tlOut.play(0);
                status = "close";
            }
        } else return;
    }
});
