const data = [
    { id: 1, text: 'Monday' },
    { id: 2, text: 'Tuesday' },
    { id: 3, text: 'Wednesday' },
    { id: 4, text: 'Thursday' },
    { id: 5, text: 'Friday' },
    { id: 6, text: 'Saturday' },
    { id: 7, text: 'Sunday' },
];
$(function () {
    const select = $('#selecteddays');
    const selectedId = $('#selectedId');
    select.select2({
        placeholder: 'Select an option',
        data: data
    })
        .on('change', (event) => {
            const selecions = select.select2('data')
                .map((element) => parseInt(element.id, 10));
            selectedId.val(selecions.join(','));
        });
});




